import { ADD_FAV, REMOVE_FAV, FILTER, ORDER } from "./action-types";

const initialState = {
    myFavorites: [],
    allCharacters: []
}

export default function rootReducer(state = initialState, { type, payload }) {
    switch (type) {
        case ADD_FAV:
            return {
                ...state,
                myFavorites: [...state.myFavorites, payload],
                allCharacters: [...state.myFavorites, payload]
            }

        case REMOVE_FAV:
            return {
                ...state,
                myFavorites: [...state.myFavorites.filter(((fav) => fav.id !== Number(payload)))],
                allCharacters: [...state.allCharacters.filter(((fav) => fav.id !== Number(payload)))]
            }
        case FILTER:
            return {
                ...state,
                myFavorites: [...state.myFavorites.fill((fav) => fav.gender === payload)],
                allCharacters: [...state.allCharacters]
            }


        default:
            return { ...state }
    }
}
